function find(e){switch(-1!==e.indexOf("=")?(type="=",attr=str.split("=")[0],val=str.split("=")[1]):-1!==e.indexOf("[")&&-1!==e.indexOf("]")?(type="[]",attr=e.replace("[","").replace("]","")):type=e.charAt(0),type){case".":return document.getElementsByClassName(e)[0];case"#":return document.getElementById(e);case"[]":return document.querySelector("["+attr+"]");case"=":return document.querySelector("["+attr+'"="'+val+'"]');default:return document.getElementsByTagName(e)[0]}}
